mapper.update:

     update tb_acc 
      
            froze_amnt = froze_amnt + #{frozeAmnt},
            total_amnt = total_amnt + #{totalAmnt},
            last_caldate = now(),
            modify_time = now(),
       
            operator = #{operator}
            
     
     where user_code = #{userCode}     
           and syscode = #{syscode}


     update tb_acc 
      
            avb_amnt = avb_amnt - #{avbAmnt},
            total_amnt = total_amnt - #{totalAmnt},
            last_caldate = now(),
            modify_time = now(),
       
            operator = #{operator}
            
     
      where user_code = #{userCode}     
           and syscode = #{syscode}

mapper.select:
 select * from tb_acc 
 where 1=1
 and  user_code = #{userCode} 

 
      and syscode = #{syscode}

mapper.update:
UPDATE tb_acc t
SET t.total_amnt = t.total_amnt - #{amount},
t.avb_amnt = t.avb_amnt - #{amount},
t.froze_amnt_out = #{amount}
where t.user_code = #{userCode}
and t.avb_amnt >#{amount}

UPDATE tb_acc t
SET t.froze_amnt_out = 0
where t.user_code = #{userCode}
