mapper.update:

     update tb_acc 
      
            avb_amnt = avb_amnt + #{avbAmnt},
            total_amnt = total_amnt + #{avbAmnt},
            last_caldate = now(),
            modify_time = now(),
       
            operator = #{operator}
            
     
     where user_code = #{userCode}     
           and sub_acc_no = #{subAccno}


     update tb_acc 
      
            avb_amnt = avb_amnt - #{avbAmnt},
            total_amnt = total_amnt - #{avbAmnt},
            last_caldate = now(),
            modify_time = now(),
       
            operator = #{operator}
            
     
     where user_code = #{userCode}     
           and sub_acc_no = #{subAccno}

mapper.select:
select * from tb_acc 
where user_code = #{userCode} 

     and sub_acc_no = #{subAccno}
