mapper.update:

     update tb_acc 
      
            avb_amnt = avb_amnt + #{avbAmnt},
            total_amnt = total_amnt + #{avbAmnt},
            last_caldate = now(),
            modify_time = now(),
       
            operator = #{operator}
            
     
     where user_code = #{userCode}     
           and sub_acc_no = #{subAccno}
           and syscode = #{syscode}


     update tb_acc 
      
            avb_amnt = avb_amnt - #{avbAmnt},
            total_amnt = total_amnt - #{avbAmnt},
            last_caldate = now(),
            modify_time = now(),
       
            operator = #{operator}
            
     
     where user_code = #{userCode}     
           and sub_acc_no = #{subAccno}
           and syscode = #{syscode}

 
     update tb_acc a,tb_unfreeze_detail b
       
             a.avb_amnt = a.avb_amnt +b.unfreeze_num,
			  a.froze_amnt = a.froze_amnt - b.unfreeze_num
             last_caldate = now(),
             modify_time = now(),
        
             operator = #{operator}
             
      
      where user_code = #{userCode}     
            and sub_acc_no = #{subAccno}
            and syscode = #{syscode}
            and status ='0'

mapper.select:
select * from tb_acc 
where user_code = #{userCode} 

     and sub_acc_no = #{subAccno}


     and syscode = #{syscode}
