mapper.update:
	       update tb_acc_detail
	       set amnt = amnt + #{amnt},
	           caldate = now(),
	           modify_time = now(),
           
               operator = #{operator}
              
	       where  user_code = #{userCode}
	       
                and    sub_accno = #{subAccno} 
           
                and    rela_user_code = #{rela_user_code}
	       
                and    bouns_source1 = #{bounsSource1}
	       
	       
                and    bouns_source2 = #{bounsSource2}

mapper.select:
select * from tc_acc_detail 
where user_code = #{userCode} 
    and rela_user_code = #{relaUsercode}

    and sub_accno = #{subAccno}


     and    bouns_source1 = #{bounsSource1}


     and    bouns_source2 = #{bounsSource2}
